<script src="<?php echo base_url(); ?>assets/plugins/ckeditor/ckeditor.js"></script><section id="middle">    <div id="content" class="dashboard padding-20">        <div class="col-md-12">            <div id="panel-1" class="panel panel-default">                <div class="panel-heading">									<span class="elipsis"><!-- panel title -->										<strong>Add Article</strong>									</span>                    <!-- right options -->                    <ul class="options pull-right list-inline">                        <li><a href="#" class="opt panel_colapse" data-toggle="tooltip" title="" data-placement="bottom" data-original-title="Colapse"></a></li>                        <li><a href="#" class="opt panel_fullscreen hidden-xs" data-toggle="tooltip" title="" data-placement="bottom" data-original-title="Fullscreen"><i class="fa fa-expand"></i></a></li>                        <li><a href="#" class="opt panel_close" data-confirm-title="Confirm" data-confirm-message="Are you sure you want to remove this panel?" data-toggle="tooltip" title="" data-placement="bottom" data-original-title="Close"><i class="fa fa-times"></i></a></li>                    </ul>                </div>                <div class="panel-body">                    <?php                    $attributes = array('class' => 'form-horizontal', 'id' => 'RecordForm');                    echo form_open('user/add',$attributes);                    ?>                    <div class="col-lg-12 error-box">                    </div>                    <div class="form-group">                        <label class="control-label col-sm-2">Article Title</label>                        <div class="col-sm-10">                            <input type="text" class="form-control" required name="article_title" value="<?php echo isset($form_data->article_title)?$form_data->article_title:''?>">                        </div>                    </div>                    <div class="form-group">                        <label class="control-label col-sm-2">Article Date</label>                        <div class="col-sm-10">                            <input type="text" class="form-control datepicker" required name="article_date" value="<?php echo isset($form_data->article_date)?$form_data->article_date:''?>">                        </div>                    </div>                    <div class="form-group">                        <label class="control-label col-sm-2">Visible in Front End</label>                        <div class="col-sm-10">                            <label class="radio">                                <input type="radio" required name="visible_in_front" value="Yes" <?php echo isset($form_data->visible_in_front) && $form_data->visible_in_front=="Yes"?'checked':''?>>                                <i></i> Yes                            </label><br>                            <label class="radio">                                <input type="radio" required name="visible_in_front" value="No" <?php echo isset($form_data->visible_in_front) && $form_data->visible_in_front=="No"?'checked':''?>>                                <i></i> No                            </label><br>                        </div>                    </div>                    <div class="form-group">                        <label class="control-label col-sm-2">Article</label>                        <div class="col-sm-12">                            <textarea class="form-control" id="ckeditor1" ><?php echo isset($form_data->article_text)?$form_data->article_text:''?></textarea>                        </div>                    </div>                    <input type="hidden" name="editordata" id="editordata" value="">                    <input type="hidden" id="RecordID" name="RecordID" value="<?php echo isset($form_data->id)?$form_data->id:''?>">                    </form>                </div>                <div class="panel-footer">                    <div class="form-group">                        <div class="col-sm-offset-2 col-sm-10">                            <input type="submit" class="btn btn-sm btn-success pull-right submit-btn" form="RecordForm"  value="Submit">                        </div>                    </div>                    <div class="clearfix"></div>                </div>            </div>            <!-- / -->        </div>    </div></section><script>    $(function () {      //  CKEDITOR.replace('CK');        var edd=CKEDITOR.replace( 'ckeditor1');        $("#RecordForm").submit(function (e) {            e.preventDefault();            var editorText = CKEDITOR.instances.ckeditor1.getData();            $("#editordata").val(editorText);            $elm=$(".submit-btn");            $elm.hide();            $(".error-box").html('');            $elm.after('<i class="fa fa-spinner fa-spin fa-1x fa-fw loading submit-loading"></i>');            $.ajax({                type	: "POST",                url		: "add",                dataType : 'json',                data	:$(this).serialize(),                success	: function (resp) {                    if(resp.success){                        _toastr(resp.msg,"bottom-right","success",false);                        setTimeout(function () {                            location.reload();                        },2000);                    }else{                        if(resp.validation_msg!==""){                            $(".error-box").html('<div class="alert alert-mini alert-danger margin-bottom-30">'+resp.validation_msg+'</div>');                        }                        _toastr(resp.msg,"bottom-right","warning",false);                    }                    $(".submit-loading").remove();                     $elm.show();                }            });        });    });</script>