                                   <option value="Personal" <?php echo isset($form_data->account_type) && $form_data->account_type=="Personal"?'selected':'';?>>Personal</option>                                </select>                            </div>                        </div>                    </div>                    <div class="row">                        <div class="form-group">                            <div class="col-md-6 col-sm-6">                                <label>Full Name</label>                                <input type="text" class="form-control"  name="first_name" value="<?php echo isset($form_data->first_name)?$form_data->first_name:'' ;?>" placeholder="First Name">                            </div>                            <div class="col-md-6 col-sm-6">                                <label>Last Name</label>                                <input type="text" class="form-control"  name="last_name" value="<?php echo isset($form_data->last_name)?$form_data->last_name:'' ;?>"  placeholder="Last Name">                            </div>                        </div>                    </div>                    <div class="row">                        <div class="form-group">                            <div class="col-md-12 col-sm-12">                                <label>Email</label>                                <input type="text" class="form-control" required name="email" value="<?php echo isset($form_data->email)?$form_data->email:'' ;?>"  placeholder="Email">                            </div>                        </div>                    </div>                    <?php if(!isset($form_data->id)){                        ?>                        <div class="row">                            <div class="form-group">                                <div class="col-md-6 col-sm-6">                                    <label>Password</label>                                    <input type="password" class="form-control" name="password"  placeholder="password">                                </div>                                <div class="col-md-6 col-sm-6">                                    <label>Confirm Password</label>                                    <input type="password" class="form-control" name="conf_password"  placeholder="confirm password">                                </div>                            </div>                        </div>                    <?php                    }?>                    <input type="hidden" id="RecordID" name="RecordID" value="<?php echo isset($form_data->id)?$form_data->id:''?>">                    </form>                </div>                <div class="panel-footer">                    <input type="submit" class="btn btn-sm btn-success pull-right submit-btn" form="RecordForm"  value="Submit">                    <div class="clearfix"></div>                </div>            </div>            <!-- / -->        </div>    </div></section><script>    $(function () {        $("#RecordForm").submit(function (e) {            e.preventDefault();            $elm=$(".submit-btn");            $elm.hide();            $(".error-box").html('');            $elm.after('<i class="fa fa-spinner fa-spin fa-1x fa-fw loading submit-loading"></i>');            $.ajax({                type	: "POST",                url		: "add",                dataType : 'json',                data	:$(this).serialize(),                success	: function (resp) {                    if(resp.success){                        _toastr(resp.msg,"bottom-right","success",false);                        setTimeout(function () {                            location.reload();                        },2000);                    }else{                        if(resp.validation_msg!==""){                            $(".error-box").html('<div class="alert alert-mini alert-danger margin-bottom-30">'+resp.validation_msg+'</div>');                        }                        _toastr(resp.msg,"bottom-right","warning",false);                    }                    $(".submit-loading").remove();                     $elm.show();                }            });        });    });</script>