<section id="middle">    <div id="content" class="dashboard padding-20">        <div class="col-md-12">            <div id="panel-1" class="panel panel-default">                <div class="panel-heading">									<span class="elipsis"><!-- panel title -->										<strong>Add Test</strong>									</span>                    <!-- right options -->                    <ul class="options pull-right list-inline">                        <li><a href="#" class="opt panel_colapse" data-toggle="tooltip" title="" data-placement="bottom" data-original-title="Colapse"></a></li>                        <li><a href="#" class="opt panel_fullscreen hidden-xs" data-toggle="tooltip" title="" data-placement="bottom" data-original-title="Fullscreen"><i class="fa fa-expand"></i></a></li>                        <li><a href="#" class="opt panel_close" data-confirm-title="Confirm" data-confirm-message="Are you sure you want to remove this panel?" data-toggle="tooltip" title="" data-placement="bottom" data-original-title="Close"><i class="fa fa-times"></i></a></li>                    </ul>                </div>                <div class="panel-body">                    <?php                    $attributes = array('class' => '', 'id' => 'RecordForm');                    echo form_open('user/add',$attributes);                    ?>                    <div class="col-lg-12 error-box">                    </div>                    <div class="row">                        <div class="form-group">                            <div class="col-md-6 col-sm-6">                                <label>Test Title</label>                                <input type="text" class="form-control"  name="test_title" value="<?php echo isset($form_data->test_title)?$form_data->test_title:'' ;?>" placeholder="Test Title">                            </div>                            <div class="col-md-6 col-sm-6">                                <label>Test Duration</label>                                <select name="test_duration"  class="form-control" title="Test Duration">                                    <option value="">-Select-</option>                                    <option value="30" <?php echo isset($form_data->test_duration) && $form_data->test_duration=="30"?'selected':'';?>>Practice Test</option>                                    <option value="180" <?php echo isset($form_data->test_duration) && $form_data->test_duration=="182"?'selected':'';?>>3 Minutes</option>                                    <option value="300" <?php echo isset($form_data->test_duration) && $form_data->test_duration=="300"?'selected':'';?>>5 Minutes</option>                                </select>                            </div>                        </div>                    </div>                    <div class="row">                        <div class="form-group">                            <div class="col-md-12 col-sm-12">                                <label>Test Text</label>                                <textarea class="form-control" rows="6" name="test_text"><?php echo isset($form_data->test_text)?$form_data->test_text:'' ;?></textarea>                            </div>                        </div>                    </div>                    <input type="hidden" id="RecordID" name="RecordID" value="<?php echo isset($form_data->id)?$form_data->id:''?>">                    </form>                </div>                <div class="panel-footer">                    <input type="submit" class="btn btn-sm btn-success pull-right submit-btn" form="RecordForm"  value="Submit">                    <div class="clearfix"></div>                </div>            </div>            <!-- / -->        </div>    </div></section><script>    $(function () {        $("#RecordForm").submit(function (e) {            e.preventDefault();            $elm=$(".submit-btn");            $elm.hide();            $(".error-box").html('');            $elm.after('<i class="fa fa-spinner fa-spin fa-1x fa-fw loading submit-loading"></i>');            $.ajax({                type	: "POST",                url		: "add",                dataType : 'json',                data	:$(this).serialize(),                success	: function (resp) {                    if(resp.success){                        _toastr(resp.msg,"bottom-right","success",false);                        setTimeout(function () {                            location.reload();                        },2000);                    }else{                        if(resp.validation_msg!==""){                            $(".error-box").html('<div class="alert alert-mini alert-danger margin-bottom-30">'+resp.validation_msg+'</div>');                        }                        _toastr(resp.msg,"bottom-right","warning",false);                    }                    $(".submit-loading").remove();                     $elm.show();                }            });        });    });</script>